@AndroidEntryPoint
class MainActivity : ComponentActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContent {
            LoompaTheme {
                // A surface container using the 'background' color from the theme
                Surface(
                    modifier = Modifier.fillMaxSize(),
                    color = MaterialTheme.colorScheme.background
                ) {
                    PagingOompaLoompas()
                }
            }
        }
    }
}

@Composable
fun PagingOompaLoompas() {
    val viewModel = hiltViewModel<OompaLoompaViewModel>()
    val oompaLoompas = viewModel.getOompaLoompas().collectAsLazyPagingItems()

    LazyColumn {
        items(count = oompaLoompas.itemCount) { index ->
            OompaLoompaCard(oompaLoompas[index])
        }

        when (val state = oompaLoompas.loadState.refresh) { //FIRST LOAD
            is LoadState.Error -> {
                //TODO Error Item
                //state.error to get error message
            }

            is LoadState.Loading -> { // Loading UI
                item {
                    Column(
                        modifier = Modifier.fillParentMaxSize(),
                        horizontalAlignment = Alignment.CenterHorizontally,
                        verticalArrangement = Arrangement.Center,
                    ) {
                        Text(
                            modifier = Modifier
                                .padding(8.dp),
                            text = stringResource(R.string.loading)
                        )

                        CircularProgressIndicator(color = MaterialTheme.colorScheme.primary)
                    }
                }
            }

            else -> {}
        }

        when (val state = oompaLoompas.loadState.append) { // Pagination
            is LoadState.Error -> {
                //TODO Pagination Error Item
                //state.error to get error message
            }

            is LoadState.Loading -> { // Pagination Loading UI
                item {
                    Column(
                        modifier = Modifier.fillMaxWidth(),
                        horizontalAlignment = Alignment.CenterHorizontally,
                        verticalArrangement = Arrangement.Center,
                    ) {
                        Text(text = stringResource(R.string.loading_more_results))

                        CircularProgressIndicator(color = MaterialTheme.colorScheme.primary)
                    }
                }
            }

            else -> {}
        }
    }
}

@Composable
fun OompaLoompaCard(oompaLoompa: OompaLoompa?) {
    var expanded by rememberSaveable { mutableStateOf(false) }
    Card(
        colors = CardDefaults.cardColors(
            containerColor = MaterialTheme.colorScheme.primary
        ),
        modifier = Modifier.padding(vertical = 4.dp, horizontal = 8.dp)
    ) {
        Row {
            Column(modifier = Modifier
                .padding(8.dp)
                .weight(1f)
                .animateContentSize(
                    animationSpec = spring(
                        dampingRatio = Spring.DampingRatioMediumBouncy,
                        stiffness = Spring.StiffnessLow
                    )
                )
            ) {
                OompaLoompaCardMain(oompaLoompa = oompaLoompa)
                if (expanded) {
                    OompaLoompaCardSecondary(oompaLoompa = oompaLoompa)
                }
            }
            IconButton(
                onClick = { expanded = !expanded }
            ) {
                Icon(
                    imageVector = if (expanded) Icons.Filled.ExpandLess else Icons.Filled.ExpandMore,
                    contentDescription = if (expanded) stringResource(R.string.show_more) else stringResource(R.string.show_less)
                )
            }
        }
    }
}

@Composable
fun OompaLoompaCardMain(oompaLoompa: OompaLoompa?) {
    Text(
        text = "${oompaLoompa?.first_name} ${oompaLoompa?.last_name}",
        fontSize = 16.sp,
    )
    Text(
        text = "${oompaLoompa?.profession} (${oompaLoompa?.gender})",
        fontSize = 12.sp,
    )
    Text(
        text = "${oompaLoompa?.id}",
        fontSize = 12.sp,
    )
}

@Composable
fun OompaLoompaCardSecondary(oompaLoompa: OompaLoompa?) {
    Row {
        Spacer(modifier = Modifier
            .height(4.dp)
            .width(8.dp))
        Column {
            SecondaryLine(line = "${stringResource(R.string.email)}: ${oompaLoompa?.email}")
            SecondaryLine(line = "${stringResource(R.string.age)}: ${oompaLoompa?.age}")
            SecondaryLine(line = "${stringResource(R.string.height)}: ${oompaLoompa?.height}")
            SecondaryLine(line = "${stringResource(R.string.country)}: ${oompaLoompa?.country}")
        }
    }
}

@Composable
fun SecondaryLine(line: String) {
    Text(text = line, fontSize = 8.sp)
}

